import React, { useEffect } from 'react'
import { useState } from 'react'
import classes from './../styles/Chat.module.css';
import axios from 'axios';

interface ChatProps {
    idInstance: string;
    apiTokenInstance: string;
    friendNumber: string
}

const Chat: React.FC<ChatProps> = ({ idInstance, apiTokenInstance, friendNumber }) => {

    const [message, setMessage] = useState('')
    const [chat, setChat] = useState({})
    const [friendMessage, setFriendMessage] = useState('')

    const sendMessage = () => {
        const endpointURL = `https://api.green-api.com/waInstance${idInstance}/sendMessage/${apiTokenInstance}`;

        const headers = {
            'Content-Type': 'application/json'
        };

        const data = {
            chatId: friendNumber + '@c.us',
            message: message
        };

        axios.post(endpointURL, data, { headers })
            .then((response) => {
                console.log(`[Response]: ${response.data}`);
            })
            .catch((error) => {
                console.error(`[ERROR ${error.response.status}]: ${error.response.statusText} ${error.response.data}`);
            });
    }

    const recieveNot = () => {
        let config = {
            method: 'get',
            maxBodyLength: Infinity,
            url: `https://api.green-api.com/waInstance${idInstance}/ReceiveNotification/${apiTokenInstance}`,
            headers: { }
          };
          
          axios.request(config)
          .then((response) => {
            console.log(JSON.stringify(response.data));
            const receiptId = response.data.receiptId
            console.log(receiptId)
            if (response.data.body.typeWebhook === 'incomingMessageReceived') {
                setFriendMessage(response.data.body.messageData.textMessageData)
            }
            deleteNot(receiptId)
          })
          .catch((error) => {
            console.log(error);
          });
          
    }

    useEffect = (()=>{},[friendMessage])

    const deleteNot = (receiptId:number) => {
        let config = {
            method: 'delete',
            maxBodyLength: Infinity,
            url: `https://api.green-api.com/waInstance${idInstance}/deleteNotification/${apiTokenInstance}/${receiptId}`,
            headers: { }
          };
          
          axios.request(config)
          .then((response) => {
            console.log(JSON.stringify(response.data));
          })
          .catch((error) => {
            console.log(error);
          });
          
    }

    const getMessage = () => {
        recieveNot()
        // deleteNot()
    }



    // setInterval(() => getMessage(),1000)

    return (
        <>
            <div className={classes.chatContainer}>
                <div className={classes.info}>Chat with: {friendNumber}</div>
                <div className={classes.chat}>

                </div>
                <div className={classes.inputContainer}>
                    <input className={classes.message} type='text' value={message} onChange={(e) => setMessage(e.target.value)} placeholder='Enter your message'></input>
                    <button className={classes.sendButton} onClick={sendMessage}>Send message</button>
                    <button className={classes.sendButton} onClick={getMessage}>Recieve message</button>
                </div>
            </div>
        </>
    )
}

export default Chat
