import React from 'react'
import { useState } from 'react'
import classes from './../styles/Chat.module.css';
import axios from 'axios';

interface ChatProps {
    idInstance: string;
    apiTokenInstance: string;
    friendNumber: string
}

const Chat: React.FC<ChatProps> = ({ idInstance, apiTokenInstance, friendNumber }) => {

    const [message, setMessage] = useState('')

    const url = `https://api.green-api.com/waInstance${idInstance}/sendMessage/${apiTokenInstance}`;
    const payload = {
        chatId: friendNumber + '@c.us',
        message: 'I use Green-API to send this message to you!'
    };

    const sendMessage = () => {
        axios.post(url, payload, {
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then((response) => {
                console.log(response.data);
            })
            .catch((error) => {
                console.error(error);
            });
    }

    return (
        <>
            <div className={classes.chatContainer}>
                <div className={classes.info}>Chat with: {friendNumber}</div>
                <div className={classes.chat}></div>
                <div className={classes.inputContainer}>
                    <input className={classes.message} type='text' value={message} onChange={(e) => setMessage(e.target.value)} placeholder='Enter your message'></input>
                </div>
            </div>
        </>
    )
}

export default Chat
