import classes from './styles/Message.module.css'

interface MessageProps { 
    isOk: boolean;
    isRead: boolean;
    children: React.ReactNode;
    whoTyped: boolean;
}

const Message:React.FC<MessageProps> = ({isOk, isRead, children, whoTyped}) => {
    return (
        <div className='flex'>
            {whoTyped ? <div className={classes.forwaredFromYou}>{children}</div> : <div className={classes.forwaredToYou}>{children}</div>}
            {(isOk && isRead) ? <div>✔✔</div> : <div>✔</div>}
        </div>
    )
}

export default Message
